INSERT INTO `#__boss_2_contents` (`id`, `name`, `slug`, `meta_title`, `meta_desc`, `meta_keys`, `userid`, `published`, `frontpage`, `featured`, `date_created`, `date_last_сomment`, `date_publish`, `date_unpublish`, `views`, `type_content`, `ordering`, `content_images`, `content_imgslider`, `content_autor`, `content_datafoto`, `content_description`)
VALUES
  (1, 'Найдя на небе конус, поддерживайте тонус!', 'Найдя на небе конус, поддерживайте тонус!', '', '', '', 62, 1, 0, 0, '2014-03-12 09:53:08', NULL,
   '2014-03-12 09:53:08', '0000-00-00 00:00:00', 28, 1, 0, '1', '', 'Robert Gendler', '2013-08-06',
   '<p style="text-align: justify;">&nbsp;Единорог - совершенно ничем не примечательное созвездие между Щенком (Малым) и Собакой (Большим Псом) на нашем небе. В это время на небе правят ярчайшие звезды - Сириус и Процион, Бетельгейзе и Ригель. Поэтому глазу Единорог представляется просто провалом в небе между Проционом и Сириусом, где смотри-не смотри, ничего особенного не сыщешь ни в очках, ни в бинокль.</p>\r\n\r\n<p style="text-align: justify;">Но астрономы нашли тут вот такое чудо - сложнейший комплекс газопылевых туманностей, подсвеченных мощной S Единорога - голубым гигантом в центре снимка. Сразу под S находится область, которую назвали &quot;Шерсть Лисы&quot; - за ее структуру и цвет. Как водится, красные газопылевые туманности светят отраженным (на пыли) и эмиссионным (облака водорода) светом. Центральное голубое свечение - типичная отражающая газовая туманность вокруг S Единорога. Сама S - часть скопления под названием NGC 2264 на расстоянии 2.5 тыс св. лет от нас.</p>\r\n\r\n<p style="text-align: justify;">Ну и сам Конус,&nbsp; конечно же - слева на снимке, пальцем обличительно указывает на S Единорога, как на возможную причину того, почему он такой, как есть. Исходя из простой логики, внутри него - звезда (или даже несколько), а также обычная область с более плотным материалом, но астрономы говорят, что в данном случае все не так очевидно, как кажется, и ищут в спектрах следы более полного объяснения существования туманности Конус.</p>\r\n\r\n<p style="text-align: right;"><em>Вокруг S Единорога - звезд незримая дорога,<br />\r\nв плотной пыли свет теряет цвет.<br />\r\nИ по спектрам поглощенья на границе просветленья<br />\r\nастрономы ищут в вакууме след.</em></p>'),
  (2, 'Око Вселенной', 'Око Вселенной', '', '', '', 62, 1, 0, 0, '2014-03-12 10:11:36', NULL, '2014-03-12 10:11:36', '0000-00-00 00:00:00', 1, 1, 0,
   '1', '', 'TeddyBear', '2010-04-06', ''),
  (3, 'Луна', 'Луна', '', '', '', 62, 1, 0, 0, '2014-03-12 10:14:15', NULL, '2014-03-12 10:14:15', '0000-00-00 00:00:00', 8, 2, 0, '',
   '[{"signature":"","file":"109637.jpg"},{"signature":"","file":"109638.jpg"},{"signature":"","file":"109639.jpg"},{"signature":"","file":"109641.jpg"},{"signature":"","file":"109642.jpg"}]',
   'Руслан Фотон', '2013-12-11', '<p>Фотографии нашей спутницы Луны</p>'),
  (4, 'Черная дыра', 'Черная дыра', '', '', '', 62, 1, 0, 0, '2014-03-12 10:20:10', NULL, '2014-03-12 10:20:10', '0000-00-00 00:00:00', 19, 2, 0, '',
   '[{"signature":"","file":"257225.jpg"},{"signature":"","file":"257226.jpg"},{"signature":"","file":"257230.jpg"},{"signature":"","file":"257231.jpg"},{"signature":"","file":"257237.jpg"},{"signature":"","file":"qwertyu.jpg"}]',
   'Иван Владимирович', '2013-03-12', '');

