CREATE TABLE IF NOT EXISTS `#__boss_4_contents` (
  `id`                INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
  `name`              TEXT,
  `slug`              VARCHAR(100)     NOT NULL,
  `meta_title`        VARCHAR(60)      NOT NULL,
  `meta_desc`         VARCHAR(200)     NOT NULL,
  `meta_keys`         VARCHAR(200)     NOT NULL,
  `userid`            INT(11) UNSIGNED          DEFAULT NULL,
  `published`         TINYINT(1)                DEFAULT '1',
  `frontpage`         TINYINT(1)                DEFAULT '0',
  `featured`          TINYINT(1)                DEFAULT '0',
  `date_created`      DATETIME                  DEFAULT NULL,
  `date_last_сomment` DATETIME                  DEFAULT NULL,
  `date_publish`      DATETIME         NOT NULL,
  `date_unpublish`    DATETIME         NOT NULL,
  `views`             INT(11) UNSIGNED          DEFAULT '0',
  `type_content`      INT(11)          NOT NULL,
  `ordering`          INT(11)          NOT NULL,
  `content_fam`       TEXT             NOT NULL,
  `content_im`        TEXT             NOT NULL,
  `content_ot`        TEXT             NOT NULL,
  `content_datar`     TEXT             NOT NULL,
  `content_fotop`     TEXT             NOT NULL,
  `content_wwwp`      TEXT             NOT NULL,
  `content_vita`      TEXT             NOT NULL,
  PRIMARY KEY (`id`),
  KEY `published` (`published`)
)
  ENGINE =MyISAM
  AUTO_INCREMENT =4
  DEFAULT CHARSET =utf8;

INSERT INTO `#__boss_4_contents` (`id`, `name`, `slug`, `meta_title`, `meta_desc`, `meta_keys`, `userid`, `published`, `frontpage`, `featured`, `date_created`, `date_last_сomment`, `date_publish`, `date_unpublish`, `views`, `type_content`, `ordering`, `content_fam`, `content_im`, `content_ot`, `content_datar`, `content_fotop`, `content_wwwp`, `content_vita`)
VALUES
  (1, 'Гагарин Юрий Алексеевич', 'Гагарин Юрий Алексеевич', '', '', '', 62, 1, 1, 1, '2014-03-13 13:19:49', NULL, '2014-03-13 13:19:49',
   '0000-00-00 00:00:00', 320, 1, 0, 'Гагарин', 'Юрий', 'Алексеевич', '1934-03-09', '1', 'http://ru.wikipedia.org/wiki/Гагарин,_Юрий_Алексеевич',
   '<p style="text-align: justify;">Ю?рий Алексе?евич Гага?рин (9 марта 1934, Клушино, Гжатский район, Западная область &mdash; 27 марта 1968, около города Киржач, Владимирская область) &mdash; советский лётчик-космонавт, Герой Советского Союза, кавалер высших знаков отличия ряда государств, почётный гражданин многих российских и зарубежных городов.</p>\r\n\r\n<p style="text-align: justify;">12 апреля 1961 года Юрий Гагарин стал первым человеком в мировой истории, совершившим полёт в космическое пространство[1][2]. Ракета-носитель &laquo;Восток&raquo; с кораблём &laquo;Восток&raquo;, на борту которого находился Гагарин, была запущена с космодрома Байконур. После 108 минут пребывания в космосе Гагарин успешно приземлился в Саратовской области, неподалёку от города Энгельса. Начиная с 12 апреля 1962 года день полёта Гагарина в космос был объявлен праздником &mdash; Днём космонавтики.</p>\r\n\r\n<p style="text-align: justify;">Первый космический полёт вызвал большой интерес во всём мире, а сам Юрий Гагарин превратился в мировую знаменитость. По приглашениям зарубежных правительств и общественных организаций он посетил около 30 стран[3]. Много у первого космонавта было поездок и внутри Советского Союза. В последующие годы Гагарин вёл большую общественно-политическую работу, окончил академию им. Жуковского, работал в ЦПК и готовился к новому полёту в космос.</p>\r\n\r\n<p style="text-align: justify;">27 марта 1968 года Юрий Гагарин погиб в авиационной катастрофе вблизи деревни Новосёлово Киржачского района Владимирской области, выполняя учебный полёт на самолёте МиГ-15УТИ под руководством опытного инструктора В. С. Серёгина. Причины и обстоятельства авиакатастрофы остаются не вполне выясненными и на сегодняшний день.</p>\r\n\r\n<p style="text-align: justify;">В связи с гибелью Гагарина в Советском Союзе был объявлен общенациональный траур (впервые в истории СССР в память о человеке, не являвшемся главой государства). В честь первого космонавта Земли были переименованы ряд населённых пунктов (включая его родной город &mdash; Гжатск), названы улицы и проспекты. В разных городах мира было установлено множество памятников Гагарину.</p>'),
  (2, 'Циолковский Константин Эдуардович', 'Циолковский Константин Эдуардович', '', '', '', 62, 1, 0, 0, '2014-03-13 13:31:35', NULL,
   '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, 1, 0, 'Циолковский', 'Константин', 'Эдуардович', '1857-09-05', '1',
   'http://ru.wikipedia.org/wiki/Циолковский,_Константин_Эдуардович',
   '<p style="text-align: justify;">Константи?н Эдуа?рдович Циолко?вский (5 (17) сентября 1857, Ижевское, Рязанская губерния, Российская империя &mdash; 19 сентября 1935, Калуга, СССР) &mdash; русский и советский учёный-самоучка и изобретатель, школьный учитель. Основоположник теоретической космонавтики[1]. Обосновал использование ракет для полётов в космос, пришёл к выводу о необходимости использования &laquo;ракетных поездов&raquo; &mdash; прототипов многоступенчатых ракет. Основные научные труды относятся к аэронавтике, ракетодинамике и космонавтике[2].</p>\r\n\r\n<p style="text-align: justify;">Представитель русского космизма, член Русского общества любителей мироведения. Автор научно-фантастических произведений, сторонник и пропагандист идей освоения космического пространства. Циолковский предлагал заселить космическое пространство с использованием орбитальных станций, выдвинул идеи космического лифта, поездов на воздушной подушке. Считал, что развитие жизни на одной из планет Вселенной достигнет такого могущества и совершенства, что это позволит преодолевать силы тяготения и распространять жизнь по Вселенной.</p>'),
  (3, 'Королёв Сергей Павлович', 'Королёв Сергей Павлович', '', '', '', 62, 1, 1, 0, '2014-03-13 13:34:48', NULL, '2014-03-13 13:34:48',
   '0000-00-00 00:00:00', 18, 1, 0, 'Королёв', 'Сергей', 'Павлович', '1907-01-12', '1', 'http://ru.wikipedia.org/wiki/Королёв,_Сергей_Павлович',
   '<p style="text-align: justify;">Серге?й Па?влович Королёв (30 декабря 1906 (12 января 1907), Житомир &mdash; 14 января 1966, Москва) &mdash; советский учёный, конструктор и организатор производства ракетно-космической техники и ракетного оружия СССР, основоположник практической космонавтики. Одна из крупнейших фигур XX века в области космического ракетостроения и кораблестроения.</p>\r\n\r\n<p style="text-align: justify;">Сергей Королёв является создателем советской ракетно-космической техники, обеспечившей стратегический паритет и сделавшей СССР передовой ракетно-космической державой, и ключевой фигурой в освоении человеком космоса, создателем практической космонавтики. Благодаря реализации его идей был осуществлён запуск первого искусственного спутника Земли и первого космонавта Юрия Гагарина.</p>\r\n\r\n<p style="text-align: justify;">Дважды Герой Социалистического Труда, лауреат Ленинской премии, академик Академии наук СССР. Член КПСС с 1953 года. Полковник.</p>');

