CREATE TABLE IF NOT EXISTS `#__boss_4_fields` (
  `fieldid`        INT(11)      NOT NULL AUTO_INCREMENT,
  `name`           VARCHAR(50)  NOT NULL DEFAULT '',
  `title`          VARCHAR(255) NOT NULL DEFAULT '',
  `display_title`  TINYINT(1)   NOT NULL DEFAULT '0',
